import React, {Component} from "react";
import styled from "styled-components";

const Tippify = (TargetComponent, {backgroundColor, fontColor, fontSize, font}) => {
    return class WithToolTip extends Component {
        constructor (props){
            super(props);
            this.StyledDiv = styled.div`
                position: relative;
                margin: 0;
                padding: 0;
                &:before, &:after {
                    opacity: 0;
                    visibility: hidden;
                    margin: 0;
                    padding: 0;
                }
                &:before {
                    position: absolute;
                    bottom: 110%;
                    /* left: -50%; */
                    margin: 0 auto;
                    padding: 0.5em; 
                    width: auto;
                    border-radius: 3px;
                    background-color: ${backgroundColor};
                    color: ${fontColor};
                    content: attr(data-icon);
                    text-align: center;
                    font-family: ${font};
                    font-size: ${fontSize};
                    line-height: 1em;
                    z-index: 99;
                }
                /* triangle border hack to make "speech bubble"  */
                &:after {
                    position: absolute;
                    bottom: 100%;
                    left: 35%;
                    width: 0;
                    border: none;
                    border-top: 10px solid ${backgroundColor};
                    border-right: 10px solid transparent;
                    border-left: 10px solid transparent;
                    content: " ";
                    font-size: 0;
                    line-height: 0;
                    margin-bottom: 0.5em;
                    z-index: 98;
                }
                &:hover:before, &:hover:after {
                    opacity: 1;
                    visibility: visible;
                }
            `;
        }
        render (){
            console.log("this.props in Tippify", this.props);
            return (<this.StyledDiv data-icon={this.props.icon}><TargetComponent {...this.props}/></this.StyledDiv>);
        }
    }
}

export default Tippify;
