import React, {Component} from "react";
import styled from "styled-components";

const Tippify = TargetComponent => {
    return class WithToolTip extends Component {
        constructor (props){
            super(props);
            this.StyledDiv = styled.div`
                position: relative;
                margin: 0;
                padding: 0;
                &:before, &:after {
                    opacity: 0;
                    visibility: hidden;
                }
                &:before {
                    position: absolute;
                    bottom: 100%;
                    left: -50%;
                    padding: 7px; 
                    width: 160px;
                    border-radius: 3px;
                    background-color: #000;
                    color: #fff;
                    content: "Whoa!!!!!!!!!!!!!";
                    text-align: center;
                    font-size: 14px;
                    line-height: 1em;
                    z-index: 99;
                }
                /* triangle border hack to make "speech bubble"  */
                &:after {
                    position: absolute;
                    bottom: 90%;
                    left: 50%;

                    width: 0;
                    border: none;
                    border-top: 10px solid #000;
                    border-right: 10px solid transparent;
                    border-left: 10px solid transparent;
                    content: " ";
                    font-size: 0;
                    line-height: 0;
                    z-index: 99;
                }
                &:hover:before, &:hover:after {
                    opacity: 1;
                    visibility: visible;
                }
            `;
        }
        render (){
            return (<this.StyledDiv><TargetComponent data-bs="now I'm a Higher Order Component!" {...this.props}/></this.StyledDiv>);
        }
    }
}

export default Tippify;
