import React, {Component} from "react";
import styled from "styled-components";

const Tip = TargetComponent => {
    return class ToolTip extends Component {
        constructor (props){
            super(props);
            this.StyledComponent = styled(TargetComponent)`
                opacity: 1;
            `;
        }
        render (){
            return (<this.StyledComponent data-bs="now I'm a Higher Order Component!" {...this.props}/>);
        }
    }
}

export default Tip;
