import React, {Component} from "react";
import styled from "styled-components";

const Tippify = TargetComponent => {
    return class WithToolTip extends Component {
        constructor (props){
            super(props);
            this.StyledComponent = styled(TargetComponent)`
                opacity: 1;
                position: relative;
                z-index: 0;
                /* &:before, &:after {
                    opacity: 0;
                    visibility: hidden;
                } */
                &:before {
                    position: relative;
                    /* bottom: 150%;*/
                    /* left: 50%; */
                    /* margin-bottom: 5px;
                    margin-left: -80px;
                    padding: 7px;  */
                    width: 160px;
                    border-radius: 3px;
                    background-color: #000;
                    color: #fff;
                    content: "Whoa!!!!!!!!!!!!!";
                    text-align: center;
                    font-size: 14px;
                    line-height: 1.2;
                }
                /* &:after {
                    position: absolute;
                    bottom: 150%;
                    left: 50%;
                    margin-left: -5px;
                    width: 0;
                    border-top: 5px solid #000;
                    border-top: 5px solid hsla(0, 0%, 20%, 0.9);
                    border-right: 5px solid transparent;
                    border-left: 5px solid transparent;
                    content: " ";
                    font-size: 0;
                    line-height: 0;
                } */
                &:hover:before, &:hover:after {
                    opacity: 1;
                    visibility: visible;
                }
            `;
        }
        render (){
            return (<this.StyledComponent data-bs="now I'm a Higher Order Component!" {...this.props}/>);
        }
    }
}

export default Tippify;
